import 'dart:async' show Timer;
import 'dart:convert';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:flutter/rendering.dart';
import 'package:flutter/material.dart';
import 'package:flutter_search_bar/flutter_search_bar.dart';

// Ours
import 'package:mega_civ_rules/models/data/chapter.dart';
import 'package:mega_civ_rules/widgets/tableofcontent/tableofcontent.dart';
import 'package:mega_civ_rules/widgets/progress/progress.dart';
import 'package:mega_civ_rules/services/chapterservice.dart';
import 'package:mega_civ_rules/services/wikipediaService.dart';
import 'package:mega_civ_rules/services/themeservice.dart';
import 'package:mega_civ_rules/services/civilizationAdvanceService.dart';
import 'package:mega_civ_rules/services/imageMemoization.dart';
import 'package:flutter/services.dart';

void main() {
  debugPaintSizeEnabled = false;
  runApp(MegaCivRules());
}

class MegaCivRules extends StatefulWidget {
  MegaCivRules({Key key}) : super(key: key);

  @override
  _MegaCivRulesState createState() => new _MegaCivRulesState();
}

class _MegaCivRulesState extends State<MegaCivRules> {
  static const splashTimeout = const Duration(milliseconds: 2000);

  static const String splashImage =
      '';

  Timer splashTimer;
  bool hasShownSplash = false;
  bool darkThemeEnabled = false;
  int sliderColor = 0;

  MaterialColor themeColor = Colors.lightGreen;

  List<Chapter> chapters = [];
  List<Chapter> wikipediaChapters = [];
  SearchBar searchBar;
  String searchString = "";
  int _page = 0;
  PageController _c;

  void loadSharedPreferences() async {
    SharedPreferences.getInstance().then((prefs) {
      setState(() {
        darkThemeEnabled = prefs.getBool('theme_dark') ?? false;
        sliderColor = prefs.getInt('theme_color') ?? 0;
        themeColor = ThemeService.getColor(sliderColor);
      });
    });
  }

  @override
  void initState() {
    _c = new PageController(
      initialPage: _page,
    );
    super.initState();
    loadSharedPreferences();
    CivilizationAdvanceService.get();

    searchBar = new SearchBar(
        inBar: false,
        buildDefaultAppBar: buildAppBar,
        setState: setState,
        clearOnSubmit: false,
        onSubmitted: onChangeSearchBar,
        onChanged: onChangeSearchBar,
        onClosed: () {
          setState(() => searchString = "");
        });
    ChapterService.get().then((chapters) {
      this.setState(() {
        this.chapters = chapters;
      });
    });
    WikipediaService.get().then((chapters) {
      this.setState(() {
        this.wikipediaChapters = chapters;
      });
    });
  }

  ThemeData getThemeData() {
    return ThemeData(
        // This is the theme of your application.
        //
        // Try running your application with "flutter run". You'll see the
        // application has a blue toolbar. Then, without quitting the app, try
        // changing the primarySwatch below to Colors.green and then invoke
        // "hot reload" (press "r" in the console where you ran "flutter run",
        // or press Run > Flutter Hot Reload in IntelliJ). Notice that the
//           counter didn't reset back to zero; the application is not restarted.
        brightness: darkThemeEnabled ? Brightness.dark : Brightness.light,
        primarySwatch: themeColor,
        primaryColor: themeColor[700],
        accentColor: themeColor[900],
        backgroundColor: themeColor[100]);
  }

  AppBar buildAppBar(BuildContext context) {
    return new AppBar(
        title: new Text('Mega Civilization Rules'),
        actions: [searchBar.getSearchAction(context)]);
  }

  void onChangeSearchBar(String value) {
    setState(() => searchString = value);
  }

  Widget getNavBar() {
    return Theme(
        data: Theme.of(context).copyWith(
            canvasColor: themeColor[700],
            primaryColor: themeColor[900],
            textTheme: Theme.of(context)
                .textTheme
                .copyWith(caption: TextStyle(color: Colors.white))),
        child: BottomNavigationBar(
            currentIndex: _page,
            onTap: (index) {
              this._c.animateToPage(index,
                  duration: const Duration(milliseconds: 300),
                  curve: Curves.easeInOut);
            },
            type: BottomNavigationBarType.fixed,
            items: [
              BottomNavigationBarItem(
                  icon: Icon(Icons.book), title: const Text('Rules')),
              BottomNavigationBarItem(
                  icon: Icon(Icons.keyboard), title: const Text('Wikipedia')),
              BottomNavigationBarItem(
                  icon: Icon(Icons.insert_chart),
                  title: const Text('Progress')),
            ]));
  }

  void _onSwitchValueChange(bool newValue) async {
    SharedPreferences.getInstance().then((prefs) {
      setState(() {
        darkThemeEnabled = newValue;
      });
      prefs.setBool('theme_dark', newValue);
    });
  }

  void _onColorSliderValueChange(double newValue) async {
    SharedPreferences.getInstance().then((prefs) {
      int rounded = newValue.round();
      setState(() {
        sliderColor = rounded;
        themeColor = ThemeService.getColor(sliderColor);
      });
      prefs.setInt('theme_color', rounded);
    });
  }

  Widget getHome() {
    return Scaffold(
      appBar: searchBar.build(context),
      body: PageView(
        controller: _c,
        onPageChanged: (newPage) {
          setState(() {
            this._page = newPage;
          });
        },
        children: getBody(),
      ),
      drawer: getDrawer(),
      bottomNavigationBar:
          getNavBar(), // This trailing comma makes auto-formatting nicer for build methods.
    );
  }

  Widget getDrawer() {
    return Drawer(
        child: ListView(
      children: <Widget>[
        ListTile(
          title: const Text('Dark Theme'),
          trailing:
              Switch(value: darkThemeEnabled, onChanged: _onSwitchValueChange),
        ),
        ListTile(
          title: const Text('Color'),
          trailing: Slider(
              min: 0.0,
              label: "lel",
              max: ThemeService.presetColors.length + .0,
              value: sliderColor + .0,
              onChanged: _onColorSliderValueChange),
        ),
        ListTile(
            title: const Text('See splash again, pliz'),
            trailing: RaisedButton(
                child: const Text('Go!'),
                onPressed: () {
                  setState(() {
                    hasShownSplash = false;
                  });
                }))
      ],
    ));
  }

  List<Widget> getBody() {
    return [
      TableOfContents(
        includeIndex: true,
        chapters: this.chapters,
        searchString: this.searchString,
      ),
      TableOfContents(
        includeIndex: false,
        chapters: this.wikipediaChapters,
        searchString: this.searchString,
      ),
      Progress()
    ];
  }

  void handleSplashTimeout() {
    setState(() {
      hasShownSplash = true;
    });
  }

  Widget getSplashImage() {
    return Container(
      decoration: BoxDecoration(
          image: DecorationImage(
              fit: BoxFit.fill,
              image: MemoryImage(Base64Decoder().convert(splashImage)))),
    );
  }

  Widget getSplashScreen() {
    splashTimer = Timer(splashTimeout, handleSplashTimeout);

    return Scaffold(
        body: Stack(
      fit: StackFit.expand,
      children: <Widget>[
        Container(decoration: BoxDecoration(color: themeColor)),
        Column(
          mainAxisAlignment: MainAxisAlignment.start,
          children: <Widget>[Expanded(flex: 2, child: getSplashImage())],
        )
      ],
    ));
  }

  @override
  Widget build(BuildContext context) {
    return getMainApp();
  }

  Widget getMainApp() {
    return MaterialApp(
        theme: getThemeData(),
        home: hasShownSplash ? getHome() : getSplashScreen());
  }
}
